import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import InfoIcon from './terry.png'; // Adjust the path as needed

const generateColor = (index) => {
  const colors = [
    '#ffcccc', '#ccffcc', '#ccccff', '#ffccff', '#ffffcc', '#ccffff',
    '#ffc0cb', '#add8e6', '#90ee90', '#dda0dd', '#ffb6c1', '#ffa07a'
  ];
  return colors[index % colors.length];
};

const SearchBar = ({ username, classesData }) => {
  const [query, setQuery] = useState('');
  const [selectedClasses, setSelectedClasses] = useState({});
  const [draggedItem, setDraggedItem] = useState(null);
  const [draggedFromZone, setDraggedFromZone] = useState(null);
  const [isDraggingOut, setIsDraggingOut] = useState(false);
  const [searchType, setSearchType] = useState('All');
  const [subject, setSubject] = useState('');
  const [showInfo, setShowInfo] = useState(null);
  const [infoContent, setInfoContent] = useState({ subject: '', className: '', units: '', description: '' });
  const [infoBoxPosition, setInfoBoxPosition] = useState({ top: 100, left: 100 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [customZones, setCustomZones] = useState([]);

  const customOptions = [
    'Year 5', 'Year 1 Summer', 'Year 2 Summer', 'Year 3 Summer', 'Year 4 Summer'
  ];

  useEffect(() => {
    if (classesData && Object.keys(classesData).length > 0 && !subject) {
      setSubject(Object.keys(classesData)[0]);
    }
  }, [classesData, subject]);

  useEffect(() => {
    const fetchSelectedClasses = async () => {
      try {
        const response = await axios.post('http://127.0.0.1:5000/getUserClasses/', { username });
        if (response.data) {
          const data = response.data;
          const updatedData = {};
          Object.keys(data).forEach(key => {
            updatedData[key] = Array.isArray(data[key]) ? data[key] : [];
          });
          setSelectedClasses(updatedData);
        }
      } catch (error) {
        console.error('Error fetching user details:', error);
      }
    };

    fetchSelectedClasses();
  }, [username]);

  const handleChange = (e) => {
    setQuery(e.target.value);
  };

  const handleDragStart = (e, item, fromZone = null) => {
    setDraggedItem(item);
    setDraggedFromZone(fromZone);
    setIsDraggingOut(false);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = async (e, toZone) => {
    e.preventDefault();
    if (draggedItem) {
      const updatedSelectedClasses = { ...selectedClasses };
      if (draggedFromZone) {
        updatedSelectedClasses[draggedFromZone] = updatedSelectedClasses[draggedFromZone].filter(item => item !== draggedItem);
      }
      if (!Array.isArray(updatedSelectedClasses[toZone])) {
        updatedSelectedClasses[toZone] = [];
      }
      updatedSelectedClasses[toZone].push(draggedItem);
      setSelectedClasses(updatedSelectedClasses);
      setDraggedFromZone(null);
      try {
        await axios.post('http://127.0.0.1:5000/updateUserClasses/', { username, selected_classes: updatedSelectedClasses });
      } catch (error) {
        console.error('Error updating selected classes:', error);
      }
    }
  };

  const handleDragOverDropZone = (e) => {
    e.preventDefault();
    setIsDraggingOut(false);
  };

  const handleDragLeaveDropZone = (e) => {
    e.preventDefault();
    setIsDraggingOut(true);
  };

  const handleDropOutside = async (e) => {
    e.preventDefault();
    if (isDraggingOut && draggedItem) {
      const updatedSelectedClasses = { ...selectedClasses };
      Object.keys(updatedSelectedClasses).forEach(zoneKey => {
        if (!Array.isArray(updatedSelectedClasses[zoneKey])) {
          updatedSelectedClasses[zoneKey] = [];
        }
        updatedSelectedClasses[zoneKey] = updatedSelectedClasses[zoneKey].filter(item => item !== draggedItem);
      });
      setSelectedClasses(updatedSelectedClasses);
      setDraggedFromZone(null);
      try {
        await axios.post('http://127.0.0.1:5000/updateUserClasses/', { username, selected_classes: updatedSelectedClasses });
      } catch (error) {
        console.error('Error updating selected classes after dropping outside:', error);
      }
    }
  };

  const getAllClasses = () => {
    let allClasses = [];
    Object.keys(classesData).forEach(subject => {
      allClasses = allClasses.concat(classesData[subject].map(item => ({ subject, item })));
    });
    return allClasses;
  };

  const filteredData = searchType === 'All'
    ? getAllClasses().filter(({ item }) => item.toLowerCase().includes(query.toLowerCase()))
    : (subject ? classesData[subject] : []).filter(item => item.toLowerCase().includes(query.toLowerCase()));

  const listItemStyle = (subject) => ({
    display: 'block',
    maxWidth: '180px', // Adjust the max width as needed
    wordWrap: 'break-word',
    border: '1px solid black',
    padding: '10px',
    margin: '6px 0',
    borderRadius: '5px',
    backgroundColor: generateColor(Object.keys(classesData).indexOf(subject)),
    cursor: 'pointer',
  });

  const gridContainerStyle = {
    display: 'grid',
    gridTemplateColumns: `auto auto repeat(4, 1fr) ${customZones.includes('Year 5') ? '1fr' : ''}`,
    gridTemplateRows: `auto repeat(3, 1fr) ${customZones.some(zone => zone.includes('Summer')) ? 'repeat(1, 1fr)' : ''}`,
    gap: '10px',
    width: 'calc(100% - 220px)', // Adjust width to account for the search column
    height: 'calc(100vh - 80px)',
    marginTop: '40px',
    boxSizing: 'border-box'
  };

  const dropZoneStyle = {
    height: '200px', // Fixed height
    width: '100%', // Fixed width
    border: '2px dashed black',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '10px',
    textAlign: 'center',
    position: 'relative',
    overflowY: 'auto',
    boxSizing: 'border-box'
  };

  const droppedItemStyle = (subject) => ({
    display: 'block',
    width: '100%',
    border: '1px solid black',
    padding: '10px',
    margin: '5px 0',
    borderRadius: '5px',
    backgroundColor: generateColor(Object.keys(classesData).indexOf(subject)),
    height: '30px', // Fixed height
  });

  const rowLabels = ['Quarter 1', 'Quarter 2', 'Quarter 3'];
  const colLabels = ['Year 1', 'Year 2', 'Year 3', 'Year 4'];

  const containerStyle = {
    display: 'flex',
    height: '100vh',
    width: '100vw',
    padding: 0, // Remove padding here
    margin: 0, // Ensure no margin
    boxSizing: 'border-box'
  };

  const searchColumnStyle = {
    marginTop: '50px', // Move the whole grey area further down
    padding: '20px', // Adjust padding here
    width: '200px',
    backgroundColor: '#f5f5f5',
    color: 'black',
    fontSize: '1em',
    borderRadius: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    height: 'calc(100% - 60px)', // Full height minus some margin
    boxSizing: 'border-box',
    overflowY: 'auto',
    flexShrink: 0 // Prevent shrinking
  };

  const gridColumnStyle = {
    flexGrow: 1,
    padding: '10px',
    boxSizing: 'border-box',
    display: 'flex',
    flexDirection: 'column',
    overflowY: 'auto',
  };

  const toggleStyle = {
    display: 'flex',
    justifyContent: 'center',
    marginBottom: '10px',
  };

  const buttonStyle = {
    marginRight: '10px',
    padding: '10px',
    cursor: 'pointer',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    transition: 'background-color 0.3s',
  };

  const listContainerStyle = {
    flexGrow: 1,
    overflowY: 'auto',
    marginTop: '10px',
  };

  const searchBarStyle = {
    marginBottom: '10px',
    padding: '10px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    width: '100%',
  };

  const selectStyle = {
    marginBottom: '10px',
    padding: '10px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    width: '100%',
  };

  const buttonHoverStyle = {
    backgroundColor: '#3399ff'
  };

  const handleInfoClick = (index, subject, className, units, description) => {
    setShowInfo(index);
    setInfoContent({ subject, className, units, description });
  };

  const closeInfoBox = () => {
    setShowInfo(null);
    setInfoContent({ subject: '', className: '', units: '', description: '' });
  };

  const handleMouseDown = (e) => {
    setIsDragging(true);
    setDragStart({ x: e.clientX - infoBoxPosition.left, y: e.clientY - infoBoxPosition.top });
  };

  const handleMouseMove = useCallback((e) => {
    if (isDragging) {
      setInfoBoxPosition({
        top: e.clientY - dragStart.y,
        left: e.clientX - dragStart.x,
      });
    }
  }, [isDragging, dragStart]);

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleCustomize = (e) => {
    const newZone = e.target.value;
    if (!customZones.includes(newZone)) {
      setCustomZones([...customZones, newZone]);
    }
  };

  useEffect(() => {
    if (isDragging) {
      window.addEventListener('mousemove', handleMouseMove);
      window.addEventListener('mouseup', handleMouseUp);
    } else {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mouseup', handleMouseUp);
    }
    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isDragging, handleMouseMove]);

  if (!classesData || Object.keys(classesData).length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div 
      onDragOver={handleDragOver}
      onDrop={handleDropOutside}
      style={containerStyle}
    >
      <div style={searchColumnStyle}>
        <div style={toggleStyle}>
          <button 
            onClick={() => setSearchType('All')} 
            style={searchType === 'All' ? { ...buttonStyle, ...buttonHoverStyle } : buttonStyle}
            onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#3399ff'}
            onMouseLeave={(e) => e.currentTarget.style.backgroundColor = '#007bff'}
          >
            All
          </button>
          <button 
            onClick={() => setSearchType('Subject')} 
            style={searchType === 'Subject' ? { ...buttonStyle, ...buttonHoverStyle } : buttonStyle}
            onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#3399ff'}
            onMouseLeave={(e) => e.currentTarget.style.backgroundColor = '#007bff'}
          >
            Subject
          </button>
          <select onChange={handleCustomize} style={buttonStyle}>
            <option value="">Customize</option>
            {customOptions.map((option, index) => (
              <option key={index} value={option}>{option}</option>
            ))}
          </select>
        </div>
        {searchType === 'Subject' && (
          <select 
            value={subject} 
            onChange={(e) => setSubject(e.target.value)}
            style={selectStyle}
          >
            {Object.keys(classesData).map((subject, index) => (
              <option key={index} value={subject}>
                {subject}
              </option>
            ))}
          </select>
        )}
        <input 
          type="text" 
          value={query} 
          onChange={handleChange} 
          placeholder="Search..." 
          style={searchBarStyle}
        />
        <div style={listContainerStyle}>
          <ul style={{ padding: 0, margin: 0, listStyleType: 'none' }}>
            {filteredData.map((dataItem, index) => {
              const itemText = dataItem.item || dataItem;
              if (!itemText) return null;

              const [className, rest] = itemText.split(' - ');
              const description = rest ? rest.split(' | ')[1] : '';
              const units = rest ? rest.split(' | ')[0].split(': ')[1] : '';
              const displayName = `${dataItem.subject || subject} - ${className}`;
              return (
                <li 
                  key={index} 
                  style={listItemStyle(dataItem.subject || subject)}
                  draggable
                  onDragStart={(e) => handleDragStart(e, { className: displayName, units, description })}
                >
                  {displayName}
                  <img 
                    src={InfoIcon} 
                    alt="info" 
                    style={{ width: '20px', height: '20px', cursor: 'pointer', marginLeft: '10px' }}
                    onClick={() => handleInfoClick(index, dataItem.subject || subject, className, units, description)}
                  />
                </li>
              );
            })}
          </ul>
        </div>
      </div>
      <div style={gridColumnStyle}>
        <div style={gridContainerStyle}>
          <div></div>
          <div></div>
          {colLabels.map((colLabel, colIndex) => (
            <div key={`col-${colIndex}`} style={{ textAlign: 'center', fontWeight: 'bold' }}>
              {colLabel}
            </div>
          ))}
          {customZones.includes('Year 5') && (
            <div key="col-Year-5" style={{ textAlign: 'center', fontWeight: 'bold' }}>
              Year 5
            </div>
          )}
          {rowLabels.map((rowLabel, rowIndex) => (
            <React.Fragment key={`row-${rowIndex}`}>
              <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'flex-end', paddingRight: '10px', fontWeight: 'bold' }}>
                {rowLabel}
              </div>
              <div></div>
              {colLabels.map((colLabel, colIndex) => {
                const zone = `${rowLabel}-${colLabel}`;
                return (
                  <div
                    key={zone}
                    style={dropZoneStyle}
                    onDragOver={handleDragOverDropZone}
                    onDragLeave={handleDragLeaveDropZone}
                    onDrop={(e) => handleDrop(e, zone)}
                  >
                    {selectedClasses[zone] && selectedClasses[zone].length > 0 ? 
                      selectedClasses[zone].map((item, index) => (
                        <div 
                          key={index} 
                          style={droppedItemStyle(item.subject || subject)}
                          draggable
                          onDragStart={(e) => handleDragStart(e, item, zone)}
                        >
                          {item.className}
                        </div>
                      )) 
                      : 'Drop items here'}
                  </div>
                );
              })}
              {customZones.includes(`Year ${rowIndex + 1} Summer`) && (
                <div
                  key={`Year ${rowIndex + 1} Summer`}
                  style={dropZoneStyle}
                  onDragOver={handleDragOverDropZone}
                  onDragLeave={handleDragLeaveDropZone}
                  onDrop={(e) => handleDrop(e, `Year ${rowIndex + 1} Summer`)}
                >
                  {selectedClasses[`Year ${rowIndex + 1} Summer`] && selectedClasses[`Year ${rowIndex + 1} Summer`].length > 0 ? 
                    selectedClasses[`Year ${rowIndex + 1} Summer`].map((item, index) => (
                      <div 
                        key={index} 
                        style={droppedItemStyle(item.subject || subject)}
                        draggable
                        onDragStart={(e) => handleDragStart(e, item, `Year ${rowIndex + 1} Summer`)}
                      >
                        {item.className}
                      </div>
                    )) 
                    : 'Drop items here'}
                </div>
              )}
            </React.Fragment>
          ))}
          {customZones.includes('Year 5') && (
            <React.Fragment>
              {rowLabels.map((rowLabel, rowIndex) => (
                <div
                  key={`Year 5-${rowLabel}`}
                  style={dropZoneStyle}
                  onDragOver={handleDragOverDropZone}
                  onDragLeave={handleDragLeaveDropZone}
                  onDrop={(e) => handleDrop(e, `Year 5-${rowLabel}`)}
                >
                  {selectedClasses[`Year 5-${rowLabel}`] && selectedClasses[`Year 5-${rowLabel}`].length > 0 ? 
                    selectedClasses[`Year 5-${rowLabel}`].map((item, index) => (
                      <div 
                        key={index} 
                        style={droppedItemStyle(item.subject || subject)}
                        draggable
                        onDragStart={(e) => handleDragStart(e, item, `Year 5-${rowLabel}`)}
                      >
                        {item.className}
                      </div>
                    )) 
                    : 'Drop items here'}
                </div>
              ))}
            </React.Fragment>
          )}
        </div>
      </div>
      {showInfo !== null && (
        <div
          style={{
            position: 'absolute',
            top: `${infoBoxPosition.top}px`,
            left: `${infoBoxPosition.left}px`,
            padding: '20px',
            width: '400px',
            backgroundColor: 'white',
            border: '1px solid #ccc',
            borderRadius: '10px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
            zIndex: 1000,
            cursor: 'move',
          }}
          onMouseDown={handleMouseDown}
        >
          <p><strong>Subject:</strong> {infoContent.subject}</p>
          <p><strong>Class Name:</strong> {infoContent.className}</p>
          <p><strong>Units:</strong> {infoContent.units}</p>
          <p><strong>Description:</strong> {infoContent.description}</p>
          <button onClick={closeInfoBox}>Close</button>
        </div>
      )}
    </div>
  );
};

export default SearchBar;
