import React, { useEffect, useState } from 'react';
import Papa from 'papaparse';
import SearchBar from './SearchBar';

const Planner = ({ username }) => {
  const [classesData, setClassesData] = useState({});

  useEffect(() => {
    const fetchClassesData = async () => {
      try {
        const response = await fetch('/classes.csv'); // Fetching from the public directory
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const reader = response.body.getReader();
        const result = await reader.read();
        const decoder = new TextDecoder('utf-8');
        const csvData = decoder.decode(result.value);

        const parsedData = Papa.parse(csvData, { header: true });
        const classesBySubject = {};
        parsedData.meta.fields.forEach(subject => {
          classesBySubject[subject] = parsedData.data.map(row => row[subject]).filter(Boolean);
        });

        setClassesData(classesBySubject);
      } catch (error) {
        console.error('Error fetching classes data:', error);
      }
    };

    fetchClassesData();
  }, []);

  return (
    <div>
      <SearchBar username={username} classesData={classesData} />
    </div>
  );
};

export default Planner;
