import React from 'react';

const About = () => {
  const containerStyle = {
    position: 'fixed',
    top: '90px', // Position it below the TopStrip
    left: '50%', // Center the container horizontally
    transform: 'translateX(-50%)', // Center the container horizontally
    padding: '20px',
    width: '80%',
    maxWidth: '600px',
    backgroundColor: '#f5f5f5', // Light grey background for contrast
    color: 'black',
    fontSize: '1.2em', // Font size
    zIndex: '1000', // Ensures the text is always on top
    fontStyle: 'italic', // Italic text
    borderRadius: '10px', // Rounded corners
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', // Subtle shadow for depth
    textAlign: 'center', // Center the text
  };

  return (
    <div style={containerStyle}>
      <h1>
        I was bored and thought it would be fun to hack this together. Msg me at
        <a href="mailto:mirbahri@ucla.edu" style={{ color: '#4db9e6', textDecoration: 'none' }}> mirbahri@ucla.edu </a>
        if you have any cool ideas to improve this project. <br /> <br />
        - mdawg96
      </h1>
    </div>
  );
};

export default About;
