import React from 'react';
import { Link } from 'react-router-dom';

const MainHeader = () => {
  const containerStyle = {
    position: 'fixed',
    top: '40%', // Center vertically
    left: '50%', // Center horizontally
    transform: 'translate(-50%, -50%)', // Offset by half its size to center perfectly
    padding: '10px',
    color: 'black',
    fontSize: '1em',
    zIndex: '1000', // Ensures the header is always on top
    fontFamily: 'Arial',
    display: 'flex', // Use flexbox for layout
    flexDirection: 'column', // Stack items vertically
    alignItems: 'center', // Center items horizontally
    justifyContent: 'center', // Center items vertically
  };

  const buttonStyle = {
    marginTop: '0px', // Adds space above the button
    padding: '5px 30px', // Smaller padding for height, wider padding for width
    fontSize: '0.8em', // Smaller font size
    cursor: 'pointer',
    border: 'none',
    borderRadius: '5px',
    backgroundColor: '#61dafb',
    color: '#fff',
    transition: 'background-color 0.3s ease',
    textDecoration: 'none', // Ensure text decoration is none for Link
    display: 'inline-block',
    textAlign: 'center',
  };

  return (
    <div style={containerStyle}>
      <h1>Bruin Planner</h1>
      <Link to="/login" style={buttonStyle}>Login</Link> {/* Single Login button */}
    </div>
  );
};

export default MainHeader;
