import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const MainHeader = () => {
  const [isHovered, setIsHovered] = useState(false);

  const containerStyle = {
    position: 'fixed',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    padding: '40px', // Increase padding for more space inside the container
    width: '80%', // Set a specific width
    maxWidth: '600px', // Set a max width for responsiveness
    color: '#333', // Darker text color for better contrast
    fontSize: '1.2em', // Increase font size
    zIndex: '1000',
    fontFamily: 'Arial, sans-serif', // Use a more modern font stack
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#FFFFFF', // White background for a clean look
    border: '2px solid #61dafb', // Add a border with the theme color
    borderRadius: '15px', // Increase border radius for smoother corners
    boxShadow: '0 8px 16px rgba(0, 0, 0, 0.2)', // Larger shadow for better depth
    textAlign: 'center', // Center text alignment
  };

  const buttonStyle = {
    marginTop: '20px',
    padding: '15px 40px', // Increase padding for a larger button
    fontSize: '1em', // Increase font size for larger text
    cursor: 'pointer',
    border: 'none',
    borderRadius: '25px', // Increase border radius for a pill-shaped button
    backgroundColor: isHovered ? '#4db9e6' : '#61dafb', // Change color based on hover state
    color: '#fff',
    textDecoration: 'none',
    display: 'inline-block',
    textAlign: 'center',
    transition: 'background-color 0.3s ease, transform 0.3s ease', // Add transform transition
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', // Add shadow for the button
    transform: isHovered ? 'scale(1.05)' : 'scale(1)', // Slightly enlarge the button on hover
  };

  return (
    <div style={containerStyle}>
      <h1 style={{ marginBottom: '20px', fontSize: '2em', color: '#61dafb' }}>Bruin Planner</h1> {/* Enhance the header style */}
      <Link
        to="/login"
        style={buttonStyle}
        onMouseEnter={() => setIsHovered(true)} // Set hover state to true on mouse enter
        onMouseLeave={() => setIsHovered(false)} // Set hover state to false on mouse leave
      >
        Login
      </Link>
    </div>
  );
};

export default MainHeader;
