import React, { useEffect, useState } from 'react';
import Papa from 'papaparse';
import SearchBar from './SearchBar';

const Planner = ({ username }) => {
  const [classesData, setClassesData] = useState({});

  useEffect(() => {
    const fetchClassesData = async () => {
      try {
        const response = await fetch('/classes.csv'); // Ensure this file path is correct
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const reader = response.body.getReader();
        const result = await reader.read();
        const decoder = new TextDecoder('utf-8');
        const csvData = decoder.decode(result.value);

        const parsedData = Papa.parse(csvData, { header: false });
        const classesBySubject = {};
        const subjects = parsedData.data[0];

        subjects.forEach((subject, index) => {
          classesBySubject[subject] = parsedData.data.slice(1).map(row => {
            const [name, units, description] = row[index].split(' - Units: ');
            return { name, units: units.split(' | ')[0], description: units.split(' | ')[1] };
          }).filter(Boolean);
        });

        setClassesData(classesBySubject);
      } catch (error) {
        console.error('Error fetching classes data:', error);
      }
    };

    fetchClassesData();
  }, []);

  return (
    <div>
      <SearchBar username={username} classesData={classesData} />
    </div>
  );
};

export default Planner;
