import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import './App.css';
import Footer from './components/Footer';
import MainHeader from './components/MainHeader';
import TopStrip from './components/TopStrip';
import Login from './components/Login'; // Ensure correct casing
import About from './components/About'; // Ensure correct casing
import Planner from './components/Planner'; // Ensure correct casing

function App() {
  const [username, setUsername] = useState('');

  return (
    <Router>
      <div className="Main">
        <TopStrip /> {/* TopStrip appears on all pages */}
        <Routes>
          <Route path="/" element={<MainHeader />} />
          <Route path="/login" element={<Login setUsername={setUsername} />} /> {/* Pass setUsername to Login */}
          <Route path="/about" element={<About />} />
          <Route path="/planner" element={<Planner username={username} />} /> {/* Pass username to Planner */}
        </Routes>
        <Footer /> {/* Footer appears on all pages */}
      </div>
    </Router>
  );
}

export default App;
