import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SearchBar = ({ username, classesData }) => {
  const [query, setQuery] = useState('');
  const [selectedClasses, setSelectedClasses] = useState({});
  const [draggedItem, setDraggedItem] = useState(null);
  const [isDraggingOut, setIsDraggingOut] = useState(false);
  const [searchType, setSearchType] = useState('All');
  const [subject, setSubject] = useState(Object.keys(classesData)[0]);

  const data = searchType === 'All' ? 
    Object.values(classesData).flat() : 
    (classesData[subject] || []);

  useEffect(() => {
    const fetchSelectedClasses = async () => {
      try {
        console.log('Fetching classes for user:', username);
        const response = await axios.post('http://127.0.0.1:5000/getUserClasses/', { username });
        if (response.data) {
          setSelectedClasses(response.data);
        }
      } catch (error) {
        console.error('Error fetching user details:', error);
      }
    };

    fetchSelectedClasses();
  }, [username]);

  const handleChange = (e) => {
    setQuery(e.target.value);
  };

  const handleDragStart = (e, item) => {
    setDraggedItem(item);
    setIsDraggingOut(false);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = async (e, zone) => {
    e.preventDefault();
    if (draggedItem) {
      const updatedSelectedClasses = { ...selectedClasses };
      if (!updatedSelectedClasses[zone]) {
        updatedSelectedClasses[zone] = [];
      }
      if (!updatedSelectedClasses[zone].includes(draggedItem)) {
        updatedSelectedClasses[zone].push(draggedItem);
        setSelectedClasses(updatedSelectedClasses);
        try {
          console.log('Updating classes for user:', username);
          const response = await axios.post('http://127.0.0.1:5000/updateUserClasses/', { username, selected_classes: updatedSelectedClasses });
          console.log('Server response:', response.data);
        } catch (error) {
          console.error('Error updating selected classes:', error);
        }
      }
    }
  };

  const handleDragOverDropZone = (e) => {
    e.preventDefault();
    setIsDraggingOut(false);
  };

  const handleDragLeaveDropZone = (e) => {
    e.preventDefault();
    setIsDraggingOut(true);
  };

  const handleDropOutside = async (e) => {
    e.preventDefault();
    if (isDraggingOut && draggedItem) {
      const updatedSelectedClasses = { ...selectedClasses };
      Object.keys(updatedSelectedClasses).forEach(zone => {
        updatedSelectedClasses[zone] = updatedSelectedClasses[zone].filter(item => item !== draggedItem);
      });
      setSelectedClasses(updatedSelectedClasses);
      try {
        console.log('Updating classes for user:', username);
        const response = await axios.post('http://127.0.0.1:5000/updateUserClasses/', { username, selected_classes: updatedSelectedClasses });
        console.log('Selected classes updated after dropping outside:', updatedSelectedClasses);
        console.log('Server response:', response.data);
      } catch (error) {
        console.error('Error updating selected classes after dropping outside:', error);
      }
    }
  };

  const filteredData = data.filter(item => 
    item.toLowerCase().includes(query.toLowerCase())
  );

  const listItemStyle = {
    display: 'block',
    width: 'fit-content',
    border: '1px solid black',
    padding: '10px',
    margin: '6px 0',
    borderRadius: '5px',
    backgroundColor: '#f9f9f9',
    cursor: 'pointer',
  };

  const gridContainerStyle = {
    display: 'grid',
    gridTemplateColumns: 'auto auto repeat(4, 1fr)',
    gridTemplateRows: 'auto repeat(3, 1fr)',
    gap: '10px',
    width: '100%',
    height: 'calc(100vh - 80px)',
    marginTop: '40px',
  };

  const dropZoneStyle = {
    height: 'calc(100% - 20px)',
    border: '2px dashed black',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'flex-start',
    padding: '10px',
    textAlign: 'center',
    position: 'relative',
  };

  const droppedItemStyle = {
    display: 'block',
    width: '100%',
    border: '1px solid black',
    padding: '10px',
    margin: '5px 0',
    borderRadius: '5px',
    backgroundColor: '#f9f9f9',
  };

  const rowLabels = ['Quarter 1', 'Quarter 2', 'Quarter 3'];
  const colLabels = ['Year 1', 'Year 2', 'Year 3', 'Year 4'];

  const containerStyle = {
    display: 'flex',
    height: '100vh',
    width: '100vw',
  };

  const searchColumnStyle = {
    display: 'flex',
    flexDirection: 'column',
    padding: '50px 20px',
    width: '200px',
    boxSizing: 'border-box',
    marginTop: '40px',
  };

  const gridColumnStyle = {
    flexGrow: 1,
    padding: '10px',
    boxSizing: 'border-box',
  };

  const toggleStyle = {
    display: 'flex',
    justifyContent: 'center',
    marginBottom: '20px',
  };

  return (
    <div 
      onDragOver={handleDragOver}
      onDrop={handleDropOutside}
      style={containerStyle}
    >
      <div style={searchColumnStyle}>
        <div style={toggleStyle}>
          <button 
            onClick={() => setSearchType('All')} 
            style={{ marginRight: '10px', padding: '10px', cursor: 'pointer' }}
          >
            All
          </button>
          <button 
            onClick={() => setSearchType('Subject')} 
            style={{ padding: '10px', cursor: 'pointer' }}
          >
            Subject
          </button>
        </div>
        {searchType === 'Subject' && (
          <select value={subject} onChange={(e) => setSubject(e.target.value)}>
            {Object.keys(classesData).map((subject, index) => (
              <option key={index} value={subject}>
                {subject}
              </option>
            ))}
          </select>
        )}
        <input 
          type="text" 
          value={query} 
          onChange={handleChange} 
          placeholder="Search..." 
          style={{ marginBottom: '20px' }}
        />
        <ul style={{ padding: 0, margin: 0, listStyleType: 'none' }}>
          {filteredData.map((item, index) => (
            <li 
              key={index} 
              style={listItemStyle}
              draggable
              onDragStart={(e) => handleDragStart(e, item)}
            >
              {item}
            </li>
          ))}
        </ul>
      </div>
      <div style={gridColumnStyle}>
        <div style={gridContainerStyle}>
          <div></div>
          <div></div>
          {colLabels.map((colLabel, colIndex) => (
            <div key={`col-${colIndex}`} style={{ textAlign: 'center', fontWeight: 'bold' }}>
              {colLabel}
            </div>
          ))}
          {rowLabels.map((rowLabel, rowIndex) => (
            <React.Fragment key={`row-${rowIndex}`}>
              <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'flex-end', paddingRight: '10px', fontWeight: 'bold' }}>
                {rowLabel}
              </div>
              <div></div>
              {colLabels.map((colLabel, colIndex) => {
                const zone = `${rowLabel}-${colLabel}`;
                return (
                  <div
                    key={zone}
                    style={dropZoneStyle}
                    onDragOver={handleDragOverDropZone}
                    onDragLeave={handleDragLeaveDropZone}
                    onDrop={(e) => handleDrop(e, zone)}
                  >
                    {selectedClasses[zone] && selectedClasses[zone].map((item, index) => (
                      <div 
                        key={index} 
                        style={droppedItemStyle}
                        draggable
                        onDragStart={(e) => handleDragStart(e, item)}
                      >
                        {item}
                      </div>
                    ))}
                    {!selectedClasses[zone] && 'Drop items here'}
                  </div>
                );
              })}
            </React.Fragment>
          ))}
        </div>
      </div>
    </div>
  );
};

export default SearchBar;
