import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import InfoIcon from './terry.png'; // Adjust the path as needed

const generateColor = (index) => {
  const colors = [
    '#ffcccc', '#ccffcc', '#ccccff', '#ffccff', '#ffffcc', '#ccffff',
    '#ffc0cb', '#add8e6', '#90ee90', '#dda0dd', '#ffb6c1', '#ffa07a'
  ];
  return colors[index % colors.length];
};

const SearchBar = ({ username, classesData }) => {
  const [query, setQuery] = useState('');
  const [selectedClasses, setSelectedClasses] = useState({});
  const [draggedItem, setDraggedItem] = useState(null);
  const [draggedFromZone, setDraggedFromZone] = useState(null);
  const [isDraggingOut, setIsDraggingOut] = useState(false);
  const [searchType, setSearchType] = useState('All');
  const [subject, setSubject] = useState('');
  const [showInfo, setShowInfo] = useState(null);
  const [infoContent, setInfoContent] = useState({ subject: '', className: '', units: '', description: '' });
  const [infoBoxPosition, setInfoBoxPosition] = useState({ top: 100, left: 100 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [customOptions, setCustomOptions] = useState([]);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  useEffect(() => {
    if (classesData && Object.keys(classesData).length > 0 && !subject) {
      setSubject(Object.keys(classesData)[0]);
    }
  }, [classesData, subject]);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.post('http://127.0.0.1:5000/getUserClasses/', { username });
        if (response.data) {
          const data = response.data;
          console.log('User data fetched:', data); // Add this line for debugging
          if (data.selected_classes) {
            const updatedClasses = {};
            Object.keys(data.selected_classes).forEach(key => {
              updatedClasses[key] = Array.isArray(data.selected_classes[key]) ? data.selected_classes[key] : [];
            });
            setSelectedClasses(updatedClasses);
          }
          if (data.custom_options) {
            setCustomOptions(data.custom_options);
          }
        }
      } catch (error) {
        console.error('Error fetching user details:', error);
      }
    };

    fetchUserData();
  }, [username]);

  const handleChange = (e) => {
    setQuery(e.target.value);
  };

  const handleDragStart = (e, item, fromZone = null) => {
    setDraggedItem(item);
    setDraggedFromZone(fromZone);
    setIsDraggingOut(false);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = async (e, toZone) => {
    e.preventDefault();
    if (draggedItem) {
      const updatedSelectedClasses = { ...selectedClasses };
      if (draggedFromZone) {
        updatedSelectedClasses[draggedFromZone] = updatedSelectedClasses[draggedFromZone].filter(item => item !== draggedItem);
      }
      if (!Array.isArray(updatedSelectedClasses[toZone])) {
        updatedSelectedClasses[toZone] = [];
      }
      updatedSelectedClasses[toZone].push(draggedItem);
      setSelectedClasses(updatedSelectedClasses);
      setDraggedFromZone(null);
      try {
        await axios.post('http://127.0.0.1:5000/updateUserClasses/', { username, selected_classes: updatedSelectedClasses, custom_options: customOptions });
      } catch (error) {
        console.error('Error updating selected classes:', error);
      }
    }
  };

  const handleDragOverDropZone = (e) => {
    e.preventDefault();
    setIsDraggingOut(false);
  };

  const handleDragLeaveDropZone = (e) => {
    e.preventDefault();
    setIsDraggingOut(true);
  };

  const handleDropOutside = async (e) => {
    e.preventDefault();
    if (isDraggingOut && draggedItem) {
      const updatedSelectedClasses = { ...selectedClasses };
      Object.keys(updatedSelectedClasses).forEach(zoneKey => {
        if (!Array.isArray(updatedSelectedClasses[zoneKey])) {
          updatedSelectedClasses[zoneKey] = [];
        }
        updatedSelectedClasses[zoneKey] = updatedSelectedClasses[zoneKey].filter(item => item !== draggedItem);
      });
      setSelectedClasses(updatedSelectedClasses);
      setDraggedFromZone(null);
      try {
        await axios.post('http://127.0.0.1:5000/updateUserClasses/', { username, selected_classes: updatedSelectedClasses, custom_options: customOptions });
      } catch (error) {
        console.error('Error updating selected classes after dropping outside:', error);
      }
    }
  };

  const getAllClasses = () => {
    let allClasses = [];
    Object.keys(classesData).forEach(subject => {
      allClasses = allClasses.concat(classesData[subject].map(item => ({ subject, item })));
    });
    return allClasses;
  };

  const filteredData = searchType === 'All'
    ? getAllClasses().filter(({ item }) => item.toLowerCase().includes(query.toLowerCase()))
    : (subject ? classesData[subject] : []).filter(item => item.toLowerCase().includes(query.toLowerCase()));

  const listItemStyle = (subject) => ({
    display: 'block',
    maxWidth: '160px', // Adjust the max width as needed
    wordWrap: 'break-word',
    border: '1px solid black',
    padding: '10px',
    margin: '6px 0',
    borderRadius: '5px',
    backgroundColor: generateColor(Object.keys(classesData).indexOf(subject)),
    cursor: 'pointer',
  });

  const gridContainerStyle = {
    display: 'grid',
    gridTemplateColumns: `auto repeat(${4 + (customOptions.includes('Year 5') ? 1 : 0)}, 1fr)`,
    gridTemplateRows: `auto repeat(${customOptions.some(option => option.includes('Summer')) ? 4 : 3}, 1fr)`,
    gap: '10px',
    width: 'calc(100% - 220px)',
    height: 'calc(100vh - 80px)',
    marginTop: '40px',
    marginLeft: '100px',
    boxSizing: 'border-box',
    justifyContent: 'start'
  };

  const dropZoneStyle = {
    height: '200px', // Fixed height
    width: '100%', // Fixed width
    border: '2px dashed black',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '10px',
    textAlign: 'center',
    position: 'relative',
    overflowY: 'auto',
    boxSizing: 'border-box'
  };

  const droppedItemStyle = (subject) => ({
    display: 'block',
    width: '100%',
    border: '1px solid black',
    padding: '10px',
    margin: '5px 0',
    borderRadius: '5px',
    backgroundColor: generateColor(Object.keys(classesData).indexOf(subject)),
    height: '30px', // Fixed height
  });

  const rowLabels = ['Quarter 1', 'Quarter 2', 'Quarter 3'];
  const colLabels = ['Year 1', 'Year 2', 'Year 3', 'Year 4'];

  const containerStyle = {
    display: 'flex',
    height: '100vh',
    width: '100vw',
    padding: 0,
    margin: 0,
    boxSizing: 'border-box'
  };

  const searchColumnStyle = {
    marginTop: '50px',
    padding: '20px',
    width: '200px',
    backgroundColor: '#f5f5f5',
    color: 'black',
    fontSize: '1em',
    borderRadius: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    height: 'calc(100% - 60px)',
    boxSizing: 'border-box',
    overflowY: 'auto',
    flexShrink: 0
  };

  const gridColumnStyle = {
    flexGrow: 1,
    padding: '10px',
    boxSizing: 'border-box',
    display: 'flex',
    flexDirection: 'column',
    overflowY: 'auto',
  };

  const buttonBaseStyle = {
    padding: '10px',
    cursor: 'pointer',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    transition: 'background-color 0.3s',
    marginRight: '10px',
  };

  const listContainerStyle = {
    flexGrow: 1,
    overflowY: 'auto',
    marginTop: '10px',
  };

  const searchBarStyle = {
    marginBottom: '10px',
    padding: '10px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    width: '100%',
    boxSizing: 'border-box',
  };

  const toggleStyle = {
    display: 'flex',
    marginBottom: '10px',
  };

  const buttonActiveStyle = {
    ...buttonBaseStyle,
    backgroundColor: '#007bff',
  };

  const infoBoxStyle = {
    position: 'absolute',
    backgroundColor: 'white',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
    padding: '10px',
    borderRadius: '5px',
    zIndex: 999,
    width: '200px',
    display: showInfo ? 'block' : 'none',
    top: infoBoxPosition.top,
    left: infoBoxPosition.left,
  };

  const showData = async (subject, className) => {
    try {
      const response = await axios.post('http://127.0.0.1:5000/getClassData/', { subject, className });
      if (response.data) {
        const data = response.data;
        console.log('Class data fetched:', data); // Add this line for debugging
        setShowInfo(data);
        setInfoContent(data);
      }
    } catch (error) {
      console.error('Error fetching class data:', error);
    }
  };

  const removeInfo = () => {
    setShowInfo(false);
    setInfoContent({ subject: '', className: '', units: '', description: '' });
  };

  return (
    <div style={containerStyle}>
      <div style={searchColumnStyle}>
        <div style={toggleStyle}>
          <button
            onClick={() => setSearchType('All')}
            style={searchType === 'All' ? buttonActiveStyle : buttonBaseStyle}
          >
            All
          </button>
          <button
            onClick={() => setSearchType('Subject')}
            style={searchType === 'Subject' ? buttonActiveStyle : buttonBaseStyle}
          >
            Subject
          </button>
        </div>
        <input
          type="text"
          placeholder={`Search ${searchType === 'All' ? 'Classes' : 'Subjects'}`}
          value={query}
          onChange={handleChange}
          style={searchBarStyle}
        />
        {searchType === 'Subject' && (
          <select
            value={subject}
            onChange={(e) => setSubject(e.target.value)}
            style={{ marginBottom: '10px', padding: '5px', width: '100%' }}
          >
            {Object.keys(classesData).map((subject, index) => (
              <option key={index} value={subject}>{subject}</option>
            ))}
          </select>
        )}
        <div style={listContainerStyle}>
          {filteredData.map(({ subject, item }, index) => (
            <div
              key={index}
              draggable
              onDragStart={(e) => handleDragStart(e, item, searchType === 'All' ? 'All' : subject)}
              onClick={() => showData(subject, item)}
              style={listItemStyle(subject)}
            >
              {item}
            </div>
          ))}
        </div>
      </div>
      <div
        style={gridColumnStyle}
        onDragOver={handleDragOverDropZone}
        onDrop={(e) => handleDropOutside(e)}
      >
        <div style={gridContainerStyle}>
          {rowLabels.map((rowLabel, rowIndex) => (
            <div key={rowIndex}>
              {colLabels.map((colLabel, colIndex) => (
                <div
                  key={colIndex}
                  style={{
                    gridColumn: `${colIndex + 1}`,
                    gridRow: `${rowIndex + 1}`,
                    border: '1px solid black',
                    padding: '10px',
                    boxSizing: 'border-box',
                    textAlign: 'center',
                    backgroundColor: '#f5f5f5',
                  }}
                  onDragOver={handleDragOverDropZone}
                  onDrop={(e) => handleDrop(e, `${rowLabel}-${colLabel}`)}
                >
                  {selectedClasses[`${rowLabel}-${colLabel}`] && selectedClasses[`${rowLabel}-${colLabel}`].map((item, index) => (
                    <div
                      key={index}
                      style={droppedItemStyle(Object.keys(classesData).findIndex(e => e.includes(item)))}
                      draggable
                      onDragStart={(e) => handleDragStart(e, item, `${rowLabel}-${colLabel}`)}
                    >
                      {item}
                    </div>
                  ))}
                </div>
              ))}
            </div>
          ))}
        </div>
      </div>
      {showInfo && (
        <div style={infoBoxStyle}>
          <img src={InfoIcon} alt="info-icon" style={{ width: '20px', float: 'right', cursor: 'pointer' }} onClick={removeInfo} />
          <h3>{infoContent.subject}</h3>
          <p><strong>Class:</strong> {infoContent.className}</p>
          <p><strong>Units:</strong> {infoContent.units}</p>
          <p><strong>Description:</strong> {infoContent.description}</p>
        </div>
      )}
    </div>
  );
};

export default SearchBar;
